mapper.update:
update prob_info
set submit_cnt = submit_cnt + 1
where id = #{id};

update prob_info
set accept_cnt = accept_cnt + 1
where id = #{id};

mapper.delete:
delete from pro_tag


        and pro_id=
        #{proId}


        and tag_id in

            #{id}

mapper.select:
select p.id, p.title, p.difficulty, p.submit_cnt, p.accept_cnt, p.time_limit, p.space_limit, p.description, p.is_delete, u1.name as create_person, p.create_date, u2.name as update_person, p.update_date, t.id as tag_id, t.name as tag_name from (select id, title, difficulty, submit_cnt, accept_cnt, time_limit, space_limit, description, create_person, create_date, update_person, update_date, is_delete from prob_info
and ( id like "%" #{keyword} "%" or title like "%" #{keyword} "%" )
and is_delete= #{isDelete}
order by id limit #{page.pageNumber}, #{page.pageSize}) as p left join user_info u1 on p.create_person = u1.id left join user_info u2 on p.update_person = u2.id left join pro_tag pt on p.id = pt.pro_id left join tag_info t on pt.tag_id = t.id;
